⋮----
anonObjs.Add(anon);
⋮----
this.RegisterEventHandlerImpl(anon, type);
⋮----
///     <see cref="RegisterEventHandler(Type)" />.
⋮----
public void RegisterEventHandler<T>()
=> this.RegisterEventHandler(typeof(T));
